mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE ${calumn} LIKE '%' || #{query} || '%'
WHERE REGDATE BETWEEN #{startDate} AND #{endDate}

SELECT B.MEMBER_NO AS MEMBERNO, B.MEMBER_ID AS MEMBERID, B.PW, B.NAME, B.EMAIL, B.REGDATE, B.MANAGER, B.STATE
  FROM (
		SELECT ROWNUM AS RN, A.MEMBER_NO, A.MEMBER_ID, A.PW, A.NAME, A.EMAIL, A.REGDATE, A.MANAGER, A.STATE
		  FROM (
				SELECT MEMBER_NO, MEMBER_ID, PW, NAME, EMAIL, REGDATE, MANAGER, STATE
				  FROM MEMBER

				 WHERE ${calumn} LIKE '%' || #{query} || '%'


				 WHERE REGDATE BETWEEN #{startDate} AND #{endDate}

				 ORDER BY ${order}

				                   DESC

			   ) A
	   ) B
 WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

SELECT MEMBER_NO AS MEMBERNO, MEMBER_ID AS MEMBERID, PW, NAME, EMAIL, REGDATE, MANAGER, STATE
  FROM MEMBER
 WHERE MEMBER_NO = #{_parameter}

mapper.update:
UPDATE MEMBER
   SET STATE = #{state}
 WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER_ADDRESS
   SET NAME = #{name}
     , ADDR1 = #{addr1}
     , ADDR2 = #{addr2}
 WHERE MEMBER_ADDRESS_NO = #{memberAddressNo}

UPDATE MEMBER_ADDRESS
   SET STATE = -1
 WHERE MEMBER_ADDRESS_NO = #{_parameter}

mapper.select:
SELECT B.NOTICE_NO AS NOTICENO, B.MEMBER_ID AS MEMBERID, B.TITLE, B.CONTENT, B.POSTDATE, B.STATE FROM ( SELECT ROWNUM AS RN, A.NOTICE_NO, A.MEMBER_ID, A.TITLE, A.CONTENT, A.POSTDATE, A.STATE FROM ( SELECT NOTICE_NO, MEMBER_ID, TITLE, CONTENT, POSTDATE, STATE FROM NOTICE
WHERE TITLE LIKE '%' || #{query} || '%' OR CONTENT LIKE '%' || #{query} || '%'
WHERE POSTDATE BETWEEN #{startDate} AND #{endDate}
WHERE ${calumn} LIKE '%' || #{query} || '%'
ORDER BY POSTDATE DESC ) A ) B WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

SELECT B.FAQ_NO AS FAQNO, B.MEMBER_ID AS MEMBERID, B.QUESTION, B.ANSWER, B.POSTDATE, B.STATE FROM ( SELECT ROWNUM AS RN, A.FAQ_NO, A.MEMBER_ID, A.QUESTION, A.ANSWER, A.POSTDATE, A.STATE FROM ( SELECT FAQ_NO, MEMBER_ID, QUESTION, ANSWER, POSTDATE, STATE FROM FAQ
WHERE QUESTION LIKE '%' || #{query} || '%' OR ANSWER LIKE '%' || #{query} || '%'
WHERE POSTDATE BETWEEN #{startDate} AND #{endDate}
WHERE ${calumn} LIKE '%' || #{query} || '%'
ORDER BY POSTDATE DESC ) A ) B WHERE RN BETWEEN #{beginRecord} AND #{endRecord}

		SELECT NOTICE_NO AS NOTICENO, MEMBER_ID AS MEMBERID, TITLE, CONTENT, POSTDATE, STATE
          FROM NOTICE
         WHERE NOTICE_NO = #{_parameter}

SELECT FAQ_NO AS FAQNO, MEMBER_ID AS MEMBERID, QUESTION, ANSWER, POSTDATE, STATE
  FROM FAQ
 WHERE FAQ_NO = #{_parameter}

mapper.update:
UPDATE NOTICE
   SET TITLE = #{title}
     , CONTENT = #{content}
 WHERE NOTICE_NO = #{noticeNo}

UPDATE FAQ
   SET TITLE = #{question}
     , CONTENT = #{answer}
 WHERE FAQ_NO = #{faqNo}
