mapper.select:
SELECT COUNT(*) FROM (SELECT DISTINCT B.PRODUCT_NO, B.PRODUCT_NAME, B.PRICE, B.BRAND, B.IMAGE FROM PRODUCT_DETAIL A INNER JOIN PRODUCT B ON A.PRODUCT_NAME = B.PRODUCT_NAME
UPPER(B.PRODUCT_NAME) LIKE '%' || UPPER(#{productName}) || '%'
AND B.BRAND IN (${brandsArray}) AND A.PRODUCT_SIZE IN (${sizesArray}) AND B.PRICE BETWEEN ${minPrice} AND ${maxPrice}
)

SELECT D.PRODUCT_NO AS PRODUCTNO, D.PRODUCT_NAME AS PRODUCTNAME, D.PRICE, D.BRAND, D.IMAGE FROM ( SELECT ROWNUM AS RN, C.PRODUCT_NO, C.PRODUCT_NAME, C.PRICE, C.BRAND, C.IMAGE FROM ( SELECT DISTINCT B.PRODUCT_NO, B.PRODUCT_NAME, B.PRICE, B.BRAND, B.IMAGE FROM PRODUCT_DETAIL A INNER JOIN PRODUCT B ON A.PRODUCT_NAME = B.PRODUCT_NAME
UPPER(B.PRODUCT_NAME) LIKE '%' || UPPER(#{productName}) || '%'
AND B.BRAND IN (${brandsArray}) AND A.PRODUCT_SIZE IN (${sizesArray}) AND B.PRICE BETWEEN ${minPrice} AND ${maxPrice}
) C ) D WHERE D.RN BETWEEN #{beginRecord} AND #{endRecord}

		SELECT MIN(A.PRICE) 
		  FROM PRODUCT_SELL A INNER JOIN PRODUCT_DETAIL B
		    ON A.PRODUCT_DETAIL_NO = B.PRODUCT_DETAIL_NO
		 WHERE B.PRODUCT_SIZE = ${param1}
		   AND B.PRODUCT_NAME = #{param2}
           AND A.STATE = 1

SELECT MAX(A.PRICE) 
  FROM PRODUCT_BUY A INNER JOIN PRODUCT_DETAIL B
    ON A.PRODUCT_DETAIL_NO = B.PRODUCT_DETAIL_NO
 WHERE B.PRODUCT_SIZE = ${param1}
   AND B.PRODUCT_NAME = #{param2}
   AND A.STATE = 0

SELECT A.PRODUCT_NAME AS PRODUCTNAME, A.BRAND, A.PRICE, A.IMAGE
  FROM PRODUCT A INNER JOIN PRODUCT_DETAIL B
    ON A.PRODUCT_NAME = B.PRODUCT_NAME
 WHERE A.PRODUCT_NAME = #{param1}
   AND B.PRODUCT_SIZE = ${param2}
