mapper.select:
	 SELECT DISTINCT B.PRODUCT_NO AS PRODUCTNO, B.PRODUCT_NAME AS productName, B.PRICE, B.BRAND, B.IMAGE
	   FROM PRODUCT_DETAIL A INNER JOIN PRODUCT B
	     ON A.PRODUCT_NAME = B.PRODUCT_NAME
	  WHERE B.PRODUCT_NAME LIKE '%' || #{PRODUCTNAME} || '%'
	   AND B.BRAND IN

		  			#{BRAND}

	   AND A.PRODUCT_SIZE IN

		  			${SIZE}

 	   AND B.PRICE BETWEEN ${MINPRICE} AND ${MAXPRICE}

SELECT PRODUCT_NO AS productNo, PRODUCT_NAME AS productName, BRAND, PRICE, IMAGE, STATE
  FROM PRODUCT
 WHERE PRODUCT_NO = #{param1}
