mapper.select:
SELECT B.REVIEW_NO AS REVIEWNO, B.MEMBER_ID AS MEMBERID, B.TITLE, B.PRODUCT_NAME AS PRODUCTNAME, B.HIT, B.POSTDATE, B.STATE FROM (SELECT ROWNUM AS RN, A.REVIEW_NO, A.MEMBER_ID, A.TITLE, A.PRODUCT_NAME, A.HIT, A.POSTDATE, A.STATE FROM (SELECT REVIEW_NO, MEMBER_ID, TITLE, PRODUCT_NAME, HIT, POSTDATE, STATE FROM REVIEW
${column} LIKE '%' || #{query} || '%'
ORDER BY REVIEW_NO DESC) A) B WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}
